inline_styles:
/* Estilos para accesibilidad - Screen Reader Only */
.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border: 0;
}

.icon {
  width: 20px;
  height: 20px;
  display: inline-block;
  vertical-align: middle;
  margin-right: 8px;
}

.logo-icon {
  width: 28px;
  height: 28px;
  margin-right: 10px;
}

.nav-link .icon {
  transition: transform 0.2s ease;
}

.nav-link:hover .icon {
  transform: scale(1.1);
}

.nav-link.active .icon {
  color: #6366f1;
}
